
const coins = require("./database.json")

module.exports = {
    Balance: function(str) {
        if (!str) {
            throw new TypeError("No message.author.id found!")
        }
        if(!coins[str]){
            coins[str] = {
                coins: 0
            }
         }
        
         let uCoins = coins[str].coins
         return uCoins;
    },
    Add: function(str, amt) {
        if (!str) {
            throw new TypeError("No message.author.id found!")
        }
        if (!amt) {
            throw new TypeError("No amount to add found!")
        }
        if(!coins[str]){
            coins[str] = {
                coins: 0
            }
         }
         coins[str] = {
            coins: coins[str].coins + amt
        }
         return `${amt} amount of coins have been added to user!`;
    },

    Subtract: function(str, amt) {
        if (!str) {
            throw new TypeError("No message.author.id found!")
        }
        if (!amt) {
            throw new TypeError("No amount to add found!")
        }
        if(!coins[str]){
            coins[str] = {
                coins: 0
            }
         }
         coins[str] = {
            coins: coins[str].coins - amt
        }
        return `${amt} amount of coins have been subtracted from user!`;
    }
}
